:root{
   --section-color:#f6f9fc;
   --p-color :hsl(215.4 16.3% 46.9%);
   --title-color : #191e33;
}
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  font-family: 'Open Sans', sans-serif;
}
html{
    scroll-behavior: smooth;
}
body {
      font-family: "Work Sans", sans-serif;
   margin: 0;
   padding:0;
   background-color: var(--section-color);

}

ul {
  list-style: none;
}

a {
  text-decoration: none;
}

::-webkit-scrollbar {
  width: 15px;
}
::-webkit-scrollbar-track {
  background-color: white;
}
::-webkit-scrollbar-thumb {
  background-color: rgb(8, 8, 29);

}

*:focus{
  outline: none;
}



/* ===== Layout ===== */
.message{
  display: flex;
  min-height: 100vh;
}

/* ===== Sidebar ===== */
.side-bar {
  width: 260px;
  background-color: white;
  color: #777;
  padding: 20px;
  position: fixed;
  top: 0;
  left: 0;
  height: 100%;
  overflow-y: auto;
      border-right: 1px solid #0000001f;
}

.side-bar h3 {
  text-align: center;
  font-size:30px;
  font-weight: 700;
  margin-bottom: 30px;
  letter-spacing: 1px;
  color: #161e2f;
}

/* Sidebar menu */
.side-bar ul {
  list-style: none;
  padding: 0;
}

.side-bar ul li {
  margin-bottom: 10px;
}

.side-bar ul li a {
  display: flex;
  align-items: center;
  gap: 12px;
  padding: 12px 16px;
  text-decoration: none;
  color: var(--p-color);
  border-radius: 8px;
  transition: 0.3s;
  font-size: 15px;
  font-weight: 500;
}

.side-bar ul li a i {
  font-size: 18px;
  min-width: 20px;
}

.side-bar ul li a:hover {
  background-color: #ebeffb;
  color: hsl(226 71% 55%);
}

.side-bar ul li a.active {
  background-color: #38bdf8;
  color: #0f172a;
}

.side-bar ul li a.active i {
  color: #0f172a;
}

/* ===== Content ===== */
.content {
  margin-left: 260px;
  flex: 1;
  padding: 20px;
  background-color: var(--section-color);
  min-height: 100vh;
}

/* ===== Header ===== */

/* ===== Section ===== */


/* ===== Responsive ===== */
@media (max-width: 768px) {
  .side-bar {
    width: 80px;
    padding: 15px 10px;
  }

  .side-bar h3 {
    font-size: 18px;
    margin-bottom: 20px;
    color: var(--title-color);
  }

  .side-bar ul li a span {
    display: none;
  }

  .side-bar ul li a {
    justify-content: center;
  }

  .content {
    margin-left: 80px;
  }
}
/* ===== Header ===== */
/* ===== Header ===== */
.admin-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 10px 20px;
  border-bottom: 1px solid #e2e8f0;
  position: sticky;
  top: 0;
  z-index: 100;
  background-color: var(--section-color);
}

/* ===== Search Box ===== */
.search-box {
  position: relative;
  width: 240px;
}

.search-box input {
 width: 100%;
    padding: 6px 30px 6px 10px;
    border-radius: 15px;
    border: 1px solid #00000047;
    outline: none;
    font-size: 14px;
    background-color: #f6f9fc;
    box-shadow: 0 0 2px #cbd5f5, 0 0 1px #0000008c;
}

.search-box input:focus {
  border-color: #94a3b8;
}

.search-box .search-icon {
  position: absolute;
  right: 8px;
  top: 50%;
  transform: translateY(-50%);
  color: #64748b;
  font-size: 14px;
  pointer-events: none;
}

/* ===== Header Right ===== */
.header-right {
  display: flex;
  align-items: center;
  gap: 12px;
}

.notification-btn {
  position: relative;
  background: none;
  border: none;
  font-size: 18px;
  cursor: pointer;
  color: #1e293b;
}

.notif-count {
  position: absolute;
  top: -4px;
  right: -6px;
  background: #ef4444;
  color: #fff;
  font-size: 10px;
  padding: 1px 4px;
  border-radius: 50%;
}

/* ===== Profile Image ===== */
.profile img {
  width: 36px;
  height: 36px;
  border-radius: 50%;
  object-fit: cover;
  cursor: pointer;
  border: 1px solid #cbd5f5;
}
section{
    padding: 20px;
}
/* main title */
.main-title{
margin-bottom: 29px;
}
.main-title h2{
color: var(--title-color);
    font-size: 28px;
    margin-bottom: 9px;

}
.main-title p{
color: var(--p-color);
}
/* messages */

/* Title */
.page-title {
  margin: 30px 0 20px;
}

/* Messages Layout */
.messages-layout {
  display: grid;
  grid-template-columns: 330px 1fr;
  gap: 25px;
}

/* Messages List */
.messages-wrapper {
  display: grid;
  grid-template-columns: 340px 1fr;
  gap: 25px;
  margin-top: 30px;
}

/* LEFT LIST */

.messages-list{
display: flex;
    flex-direction: column;
    gap: 10px;
}
.message-card{
    background-color: white;
    border-radius: 10px;
    padding: 20px;
    border: 1px solid #ddd;
}
.no-border {
  border: none !important;

}

.message-card .msg-header{
    display: flex;
    justify-content: space-between;
}
.message-card .msg-header h4{
margin-bottom: 8px;
font-size: 18px;

}
.message-card h4{
  margin-bottom: 8px; 
  font-size: 18px;
}
.message-card .msg-header .badge {

}
.message-card .msg-header .new {
    color: white;
    background: #3c3cf4;
    font-size: 13px;
    border-radius: 10px;
    height: fit-content;
    padding: 3px 8px;

}
.btn-card{
      cursor: pointer;
    border: none;
    text-align: left;
}

.message-card p ,
.message-card .date{
    color: var(--p-color);
    margin-bottom: 7px;
    font-size: 15px;
}

.message-content-box{
   background-color: white;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    gap: 10px;
    color: var(--p-color);
    font-size: 18px;
    border-radius: 13px;
    border: 2px solid #ddd;
}

.message-content-box.active {
    display: flex;
    justify-content: space-between;
    align-items: flex-start;

}

.admin-reply {
 background: #fff;
    padding: 20px;
    border-top: 1px solid #ddd;

}


.admin-reply p {
     color: #242424;
    line-height: 1.6;
    margin-bottom: 28px;
    font-size: 18px;
}

.reply-actions {
  display: flex;
  gap: 10px;
      border-top: 1px solid #ddd;
    padding: 17px 0;
}

.reply-actions button {
 padding: 7px 12px;

    border-radius: 6px;
    cursor: pointer;
    width: 48%;
    margin: auto;
    background-color: white;
    font-size: 15px;
    font-weight: 500;

}

.reply-actions .mark-read {
 color: black;
 border: 1px solid black;
}

.reply-actions .delete {
  color: red;
  border: 1px solid red;
}
