:root{
   --section-color:#f6f9fc;
   --p-color :hsl(215.4 16.3% 46.9%);
   --title-color : #191e33;
}
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  font-family: 'Open Sans', sans-serif;
}
html{
    scroll-behavior: smooth;
}
body {
      font-family: "Work Sans", sans-serif;
   margin: 0;
   padding:0;
   background-color: var(--section-color);

}

ul {
  list-style: none;
}

a {
  text-decoration: none;
}

::-webkit-scrollbar {
  width: 15px;
}
::-webkit-scrollbar-track {
  background-color: white;
}
::-webkit-scrollbar-thumb {
  background-color: rgb(8, 8, 29);

}

*:focus{
  outline: none;
}



/* ===== Layout ===== */
.orders {
  display: flex;
  min-height: 100vh;
}

/* ===== Sidebar ===== */
.side-bar {
  width: 260px;
  background-color: white;
  color: #777;
  padding: 20px;
  position: fixed;
  top: 0;
  left: 0;
  height: 100%;
  overflow-y: auto;
      border-right: 1px solid #0000001f;
}

.side-bar h3 {
  text-align: center;
  font-size:30px;
  font-weight: 700;
  margin-bottom: 30px;
  letter-spacing: 1px;
  color: #161e2f;
}

/* Sidebar menu */
.side-bar ul {
  list-style: none;
  padding: 0;
}

.side-bar ul li {
  margin-bottom: 10px;
}

.side-bar ul li a {
  display: flex;
  align-items: center;
  gap: 12px;
  padding: 12px 16px;
  text-decoration: none;
  color: var(--p-color);
  border-radius: 8px;
  transition: 0.3s;
  font-size: 15px;
  font-weight: 500;
}

.side-bar ul li a i {
  font-size: 18px;
  min-width: 20px;
}

.side-bar ul li a:hover {
  background-color: #ebeffb;
  color: hsl(226 71% 55%);
}

.side-bar ul li a.active {
  background-color: #38bdf8;
  color: #0f172a;
}

.side-bar ul li a.active i {
  color: #0f172a;
}

/* ===== Content ===== */
.content {
  margin-left: 260px;
  flex: 1;
  padding: 20px;
  background-color: var(--section-color);
  min-height: 100vh;
}

/* ===== Header ===== */

/* ===== Section ===== */


/* ===== Responsive ===== */
@media (max-width: 768px) {
  .side-bar {
    width: 80px;
    padding: 15px 10px;
  }

  .side-bar h3 {
    font-size: 18px;
    margin-bottom: 20px;
    color: var(--title-color);
  }

  .side-bar ul li a span {
    display: none;
  }

  .side-bar ul li a {
    justify-content: center;
  }

  .content {
    margin-left: 80px;
  }
}
