/* ===== Header ===== */
.admin-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 10px 20px;
  border-bottom: 1px solid #e2e8f0;
  position: sticky;
  top: 0;
  z-index: 100;
  background-color: var(--section-color);
}

/* ===== Search Box ===== */
.search-box {
  position: relative;
  width: 240px;
}

.search-box input {
 width: 100%;
    padding: 6px 30px 6px 10px;
    border-radius: 15px;
    border: 1px solid #00000047;
    outline: none;
    font-size: 14px;
    background-color: #f6f9fc;
    box-shadow: 0 0 2px #cbd5f5, 0 0 1px #0000008c;
}

.search-box input:focus {
  border-color: #94a3b8;
}

.search-box .search-icon {
  position: absolute;
  right: 8px;
  top: 50%;
  transform: translateY(-50%);
  color: #64748b;
  font-size: 14px;
  pointer-events: none;
}

/* ===== Header Right ===== */
.header-right {
  display: flex;
  align-items: center;
  gap: 12px;
}

.notification-btn {
  position: relative;
  background: none;
  border: none;
  font-size: 18px;
  cursor: pointer;
  color: #1e293b;
}

.notif-count {
  position: absolute;
  top: -4px;
  right: -6px;
  background: #ef4444;
  color: #fff;
  font-size: 10px;
  padding: 1px 4px;
  border-radius: 50%;
}

/* ===== Profile Image ===== */
.profile img {
  width: 36px;
  height: 36px;
  border-radius: 50%;
  object-fit: cover;
  cursor: pointer;
  border: 1px solid #cbd5f5;
}
