/* assets/css/pages/dashboard.css */

:root {
    --primary-color: #0075FF;
    --secondary-color: #F7F8FA;
    --text-color: #223A58;
    --text-light: #6B7280;
    --border-color: #E5E7EB;
    --success-color: #10B981;
    --warning-color: #F59E0B;
    --danger-color: #EF4444;
    --white: #FFFFFF;
    --sidebar-width: 260px;
    --header-height: 80px;
}

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Inter', 'Segoe UI', sans-serif;
    background: var(--secondary-color);
    color: var(--text-color);
    line-height: 1.6;
    display: flex;
    min-height: 100vh;
}
ul {
  list-style: none;
}

a {
  text-decoration: none;
}

::-webkit-scrollbar {
  width: 15px;
}
::-webkit-scrollbar-track {
  background-color: white;
}
::-webkit-scrollbar-thumb {
  background-color: var(--main-color);

}

*:focus{
  outline: none;
}
/* start side bare */
.dashboard-container{
  display: flex;
  min-height: 100vh;

}
.side-bar{
background-color: white;
padding: 20px 0;
position: relative;
width: 230px;
box-shadow: 0 0 10px #ddd;


}
@media(max-width:767px){
.side-bar {
    width: fit-content;
    padding: 10px 2px;
  }
}
@media(min-width:767px) and (max-width:991px){
  .side-bar {
    width: 200px;
            padding: 10px 0;
  }
}
.side-bar .logo{
color: var(--main-color);
    font-size: 35px;
    text-align: center;
    border-bottom: 1px solid #eee;
    padding-bottom: 50px;
}
@media(max-width:767px){
.side-bar .logo {
  font-size: 16px;
    padding-bottom: 15px; 
  }
}
.side-bar ul{
  padding: 0;
}
.side-bar ul li{
    padding: 20px;
    border-bottom: 1px solid #eee;
    transition: var(--main-transition-duration);

}
.side-bar ul li:hover{
  background-color: var(--main-color);
}
.side-bar ul li:hover a {
  color: white;
}
.side-bar ul li a{
text-align: center;
display: flex;
    font-size: 17px;
    color: black;
    font-weight: 500;

}
.side-bar ul li a i{
  margin-right: 15px;
}
.side-bar ul li span{
   display: block;
   transition: var(--main-transition-duration);
}
@media(max-width:767px){
  .side-bar ul li a span {
    display: none;
  } 
}

/* end side bare */

.nav-item {
    display: flex;
    align-items: center;
    gap: 1rem;
    padding: 0.875rem 1.5rem;
    color: var(--text-color);
    text-decoration: none;
    transition: all 0.3s;
    font-weight: 500;
}

.nav-item:hover {
    background: var(--secondary-color);
    color: var(--primary-color);
}

.nav-item.active {
    background: linear-gradient(90deg, rgba(0, 117, 255, 0.1), transparent);
    color: var(--primary-color);
    border-right: 3px solid var(--primary-color);
}

.sidebar-footer {
    padding: 1.5rem;
    border-top: 1px solid var(--border-color);
}

.logout-btn {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    padding: 0.875rem 1rem;
    color: var(--danger-color);
    text-decoration: none;
    border-radius: 8px;
    transition: all 0.3s;
    font-weight: 500;
}

.logout-btn:hover {
    background: rgba(239, 68, 68, 0.1);
}

/* Main Content */
.main-content {
    flex: 1;
    margin-left: var(--sidebar-width);
    padding: 2rem;
}

/* Dashboard Header */
.dashboard-header {
    background: var(--white);
    padding: 1.5rem 2rem;
    border-radius: 12px;
    margin-bottom: 2rem;
    display: flex;
    justify-content: space-between;
    align-items: center;
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.05);
}

.header-left h1 {
    font-size: 1.75rem;
    margin-bottom: 0.25rem;
}

.header-left p {
    color: var(--text-light);
}

.header-right {
    display: flex;
    align-items: center;
    gap: 1.5rem;
}

.search-box {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    background: var(--secondary-color);
    padding: 0.625rem 1rem;
    border-radius: 10px;
    min-width: 300px;
}

.search-box input {
    border: none;
    background: none;
    outline: none;
    flex: 1;
    font-size: 0.95rem;
}

.notifications-btn {
    position: relative;
    background: var(--secondary-color);
    border: none;
    width: 45px;
    height: 45px;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    cursor: pointer;
    transition: all 0.3s;
}

.notifications-btn:hover {
    background: var(--primary-color);
    color: var(--white);
}

.notifications-btn .badge {
    position: absolute;
    top: 5px;
    right: 5px;
    background: var(--danger-color);
    color: var(--white);
    font-size: 0.75rem;
    padding: 0.125rem 0.375rem;
    border-radius: 10px;
    font-weight: 600;
}

.user-profile img {
    width: 45px;
    height: 45px;
    border-radius: 50%;
    object-fit: cover;
    border: 2px solid var(--primary-color);
}

/* Stats Section */
.stats-section {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 1.5rem;
    margin-bottom: 2rem;
}

.stat-card {
    background: var(--white);
    padding: 1.75rem;
    border-radius: 12px;
    display: flex;
    align-items: center;
    gap: 1.25rem;
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.05);
    transition: transform 0.3s;
}

.stat-card:hover {
    transform: translateY(-5px);
}

.stat-icon {
    width: 60px;
    height: 60px;
    border-radius: 12px;
    display: flex;
    align-items: center;
    justify-content: center;
}

.stat-icon.blue {
    background: rgba(0, 117, 255, 0.1);
    color: var(--primary-color);
}

.stat-icon.green {
    background: rgba(16, 185, 129, 0.1);
    color: var(--success-color);
}

.stat-icon.purple {
    background: rgba(139, 92, 246, 0.1);
    color: #8B5CF6;
}

.stat-icon.orange {
    background: rgba(245, 158, 11, 0.1);
    color: var(--warning-color);
}

.stat-info h3 {
    font-size: 2rem;
    margin-bottom: 0.25rem;
}

.stat-info p {
    color: var(--text-light);
    font-size: 0.9rem;
}

/* Content Grid */
.content-grid {
    display: grid;
    grid-template-columns: 1fr 350px;
    gap: 2rem;
}

/* Courses Section */
.courses-section {
    background: var(--white);
    padding: 1.75rem;
    border-radius: 12px;
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.05);
}

.section-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 1.5rem;
}

.section-header h2 {
    font-size: 1.5rem;
}

.view-all {
    color: var(--primary-color);
    text-decoration: none;
    font-weight: 600;
}

.courses-list {
    display: flex;
    flex-direction: column;
    gap: 1.5rem;
}

.course-item {
    display: grid;
    grid-template-columns: 100px 1fr auto;
    gap: 1.25rem;
    padding: 1.25rem;
    background: var(--secondary-color);
    border-radius: 10px;
    align-items: center;
}

.course-item img {
    width: 100px;
    height: 70px;
    border-radius: 8px;
    object-fit: cover;
}

.course-details h3 {
    font-size: 1.1rem;
    margin-bottom: 0.25rem;
}

.course-details p {
    color: var(--text-light);
    font-size: 0.875rem;
    margin-bottom: 0.75rem;
}

.progress-bar {
    width: 100%;
    height: 8px;
    background: #E5E7EB;
    border-radius: 10px;
    overflow: hidden;
    margin-bottom: 0.5rem;
}

.progress {
    height: 100%;
    background: linear-gradient(90deg, var(--primary-color), #00C6FF);
    border-radius: 10px;
    transition: width 0.3s;
}

.course-meta {
    display: flex;
    justify-content: space-between;
    font-size: 0.875rem;
    color: var(--text-light);
}

.continue-btn {
    padding: 0.625rem 1.5rem;
    background: var(--primary-color);
    color: var(--white);
    border: none;
    border-radius: 8px;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s;
    white-space: nowrap;
}

.continue-btn:hover {
    background: #0056CC;
    transform: translateY(-2px);
}

/* Sidebar Content */
.sidebar-content {
    display: flex;
    flex-direction: column;
    gap: 1.5rem;
}

.activity-section,
.certificates-section,
.events-section {
    background: var(--white);
    padding: 1.5rem;
    border-radius: 12px;
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.05);
}

.activity-section h3,
.certificates-section h3,
.events-section h3 {
    font-size: 1.25rem;
    margin-bottom: 1.25rem;
}

/* Activity List */
.activity-list {
    display: flex;
    flex-direction: column;
    gap: 1rem;
}

.activity-item {
    display: flex;
    gap: 1rem;
    padding: 0.875rem;
    background: var(--secondary-color);
    border-radius: 8px;
}

.activity-icon {
    width: 36px;
    height: 36px;
    border-radius: 8px;
    display: flex;
    align-items: center;
    justify-content: center;
    flex-shrink: 0;
}

.activity-icon.completed {
    background: rgba(16, 185, 129, 0.1);
    color: var(--success-color);
}

.activity-icon.certificate {
    background: rgba(245, 158, 11, 0.1);
    color: var(--warning-color);
}

.activity-icon.started {
    background: rgba(0, 117, 255, 0.1);
    color: var(--primary-color);
}

.activity-details p {
    margin-bottom: 0.25rem;
}

.activity-details span {
    color: var(--text-light);
    font-size: 0.875rem;
}

.activity-details time {
    display: block;
    color: var(--text-light);
    font-size: 0.75rem;
    margin-top: 0.25rem;
}

/* Certificates */
.certificates-list {
    display: flex;
    flex-direction: column;
    gap: 1rem;
}

.certificate-card {
    display: flex;
    align-items: center;
    gap: 1rem;
    padding: 1rem;
    background: var(--secondary-color);
    border-radius: 8px;
}

.certificate-card svg {
    color: var(--primary-color);
}

.certificate-card h4 {
    font-size: 1rem;
    margin-bottom: 0.25rem;
}

.certificate-card p {
    color: var(--text-light);
    font-size: 0.875rem;
}

.download-btn {
    margin-left: auto;
    background: var(--primary-color);
    color: var(--white);
    border: none;
    width: 36px;
    height: 36px;
    border-radius: 8px;
    display: flex;
    align-items: center;
    justify-content: center;
    cursor: pointer;
    transition: all 0.3s;
}

.download-btn:hover {
    background: #0056CC;
    transform: scale(1.1);
}

/* Events */
.event-card {
    display: flex;
    gap: 1rem;
    padding: 1rem;
    background: var(--secondary-color);
    border-radius: 8px;
    margin-bottom: 1rem;
}

.event-date {
    width: 60px;
    height: 60px;
    background: var(--primary-color);
    color: var(--white);
    border-radius: 8px;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    flex-shrink: 0;
}

.event-date .day {
    font-size: 1.5rem;
    font-weight: 700;
    line-height: 1;
}

.event-date .month {
    font-size: 0.75rem;
    font-weight: 600;
}

.event-details h4 {
    margin-bottom: 0.25rem;
}

.event-details p {
    color: var(--text-light);
    font-size: 0.875rem;
}

/* Responsive */
@media (max-width: 1280px) {
    .content-grid {
        grid-template-columns: 1fr;
    }

    .sidebar-content {
        grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
        display: grid;
    }
}

@media (max-width: 768px) {
    .sidebar {
        transform: translateX(-100%);
        transition: transform 0.3s;
    }

    .sidebar.active {
        transform: translateX(0);
    }

    .main-content {
        margin-left: 0;
        padding: 1rem;
    }

    .dashboard-header {
        flex-direction: column;
        gap: 1rem;
    }

    .header-right {
        width: 100%;
        justify-content: space-between;
    }

    .search-box {
        min-width: auto;
        flex: 1;
    }

    .stats-section {
        grid-template-columns: 1fr;
    }

    .course-item {
        grid-template-columns: 1fr;
    }

    .continue-btn {
        width: 100%;
    }
}
