 .modal {

  position: fixed;
  top:0;
  left:0;
  width:100%;
  height:100%;
  background: rgba(0,0,0,0.6);
  /* display: flex; */
  display: none;
  align-items: center;
  justify-content: center;
  z-index: 1000;
}

.modal-content {
  background: #fff;
  padding: 25px;
  border-radius: 10px;
  width: 90%;
  max-width: 400px;
  box-shadow: 0 5px 15px rgba(0,0,0,0.3);
  position: relative;
  animation: slideDown var(--main-transition-duration) ease;
  
}


@keyframes slideDown {
  from {
     transform: translateY(-50px); opacity: 0;
     }
  to { 
    transform: translateY(0); opacity: 1;
   }
}


#close {
  position: absolute;
