    font-size: 1.5rem;
    margin-bottom: 1.5rem;
    padding-bottom: 1rem;
    border-bottom: 2px solid var(--border-color);
}

/* Promo Code */
.promo-code-section {
    margin-bottom: 2rem;
}

.promo-code-section label {
    display: block;
    margin-bottom: 0.5rem;
    font-weight: 600;
    color: var(--text-color);
}

.promo-input-group {
    display: flex;
    gap: 0.5rem;
}

.promo-input-group input {
    flex: 1;
    padding: 0.875rem;
    border: 1px solid var(--border-color);
    border-radius: 8px;
    font-size: 0.95rem;
}

.apply-btn {
    padding: 0.875rem 1.5rem;
    background: var(--secondary-color);
    border: 1px solid var(--border-color);
    border-radius: 8px;
    cursor: pointer;
    font-weight: 600;
    transition: all 0.3s;
}

.apply-btn:hover {
    background: var(--primary-color);
    color: var(--white);
    border-color: var(--primary-color);
}

.promo-message {
    margin-top: 0.5rem;
    font-size: 0.875rem;
}

.promo-message.success {
    color: var(--success-color);
}

.promo-message.error {
    color: var(--danger-color);
}

/* Summary Details */
.summary-details {
    margin-bottom: 2rem;
}

.summary-row {
    display: flex;
    justify-content: space-between;
    margin-bottom: 1rem;
    font-size: 1rem;
}

.summary-row span:first-child {
    color: var(--text-light);
}

.summary-row span:last-child {
    font-weight: 600;
    color: var(--text-color);
}

.discount-row .discount {
    color: var(--success-color);
}

.summary-total {
    display: flex;
    justify-content: space-between;
    padding-top: 1.5rem;
    border-top: 2px solid var(--border-color);
    margin-top: 1.5rem;
    font-size: 1.5rem;
    font-weight: 700;
}

.summary-total span:last-child {
    color: var(--primary-color);
}

/* Checkout Button */
.checkout-btn {
    width: 100%;
    padding: 1.125rem;
    background: linear-gradient(135deg, var(--primary-color), #0056CC);
    color: var(--white);
    border: none;
    border-radius: 10px;
    font-size: 1.1rem;
    font-weight: 700;
    cursor: pointer;
    transition: all 0.3s;
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 0.75rem;
}

.checkout-btn:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 20px rgba(0, 117, 255, 0.3);
}

.continue-shopping {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 0.5rem;
    margin-top: 1.25rem;
    color: var(--primary-color);
    text-decoration: none;
    font-weight: 500;
}

.continue-shopping:hover {
    text-decoration: underline;
}

/* Security Badges */
.security-badges {
    display: flex;
    gap: 1rem;
    margin-top: 2rem;
    padding-top: 1.5rem;
    border-top: 1px solid var(--border-color);
}

.badge {
    flex: 1;
    display: flex;
    align-items: center;
    gap: 0.5rem;
    font-size: 0.875rem;
    color: var(--text-light);
}

/* Recommended Section */
.recommended-section {
    padding: 4rem 0;
    background: var(--white);
}

.recommended-section h2 {
    font-size: 2rem;
    margin-bottom: 2rem;
    text-align: center;
}

.courses-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 2rem;
}

.course-card {
    background: var(--white);
    border-radius: 12px;
    overflow: hidden;
    box-shadow: var(--shadow-sm);
    transition: transform 0.3s;
}

.course-card:hover {
    transform: translateY(-5px);
    box-shadow: var(--shadow-md);
}

.course-card img {
    width: 100%;
    height: 200px;
    object-fit: cover;
}

.course-info {
    padding: 1.5rem;
}

.course-info h3 {
    font-size: 1.1rem;
    margin-bottom: 1rem;
}

.course-info .price {
    font-size: 1.5rem;
    font-weight: 700;
    color: var(--primary-color);
    margin-bottom: 1rem;
}

.add-to-cart-btn {
    width: 100%;
    padding: 0.75rem;
    background: var(--primary-color);
    color: var(--white);
    border: none;
    border-radius: 8px;
    font-weight: 600;
    cursor: pointer;
    transition: background 0.3s;
}

.add-to-cart-btn:hover {
    background: #0056CC;
}

/* Footer */
.main-footer {
    background: var(--text-color);
    color: var(--white);
    padding: 3rem 0 1.5rem;
}

.footer-content {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 2rem;
    margin-bottom: 2rem;
}

.footer-section h3,
.footer-section h4 {
    margin-bottom: 1rem;
}

.footer-section ul {
    list-style: none;
}

.footer-section ul li {
    margin-bottom: 0.5rem;
}

.footer-section a {
    color: var(--white);
    text-decoration: none;
    opacity: 0.8;
    transition: opacity 0.3s;
}

.footer-section a:hover {
    opacity: 1;
}

.footer-bottom {
    text-align: center;
    padding-top: 2rem;
    border-top: 1px solid rgba(255, 255, 255, 0.1);
}

/* Responsive */
@media (max-width: 1024px) {
    .cart-layout {
        grid-template-columns: 1fr;
    }

    .order-summary {
        position: static;
    }
}

@media (max-width: 768px) {
    .nav-links {
        display: none;
    }

    .cart-item {
        grid-template-columns: 100px 1fr;
        gap: 1rem;
    }

    .item-image {
        width: 100px;
        height: 75px;
    }

    .item-actions {
        grid-column: 1 / -1;
        flex-direction: row;
        justify-content: space-between;
        margin-top: 1rem;
    }

    .courses-grid {
        grid-template-columns: 1fr;
    }
}

@media (max-width: 480px) {
    .container {
        padding: 0 1rem;
    }

    .page-header h1 {
        font-size: 1.75rem;
    }

    .cart-items-container,
    .order-summary {
        padding: 1.5rem;
    }
}
