commits.content:
:root {
  --main-color: #0075FF;
  --section-color: #F7F8FA;
  --font-color: #223A58;
  --main-transition-duration: 0.3s; 
  --footer-color:#191919;
    --secondary-color: #e3f2fd;
      --text-color: #333;
      --shadow: 0 8px 20px rgba(0,0,0,0.1);
      --border-color: #ddd;

}
*{
  box-sizing: border-box;
  padding:0;
  margin: 0;
}
html{
    scroll-behavior: smooth;
}
body {
      font-family: "Work Sans", sans-serif;
   margin: 0;
   padding:20px;
   background-color: var(--section-color);
   display: flex;
    justify-content: center;
      align-items: center;
      min-height: 100vh;
}

ul {
  list-style: none;
}

a {
  text-decoration: none;
}

::-webkit-scrollbar {
  width: 15px;
}
::-webkit-scrollbar-track {
  background-color: white;
}
::-webkit-scrollbar-thumb {
  background-color: var(--main-color);

}

*:focus{
  outline: none;
}



    .payment-container {
      background: #fff;
      padding: 40px;
      border-radius: 20px;
      box-shadow: var(--shadow);
      max-width: 700px;
      width: 100%;
      position: relative;
      overflow: hidden;
    }

    .payment-container::before {
      content: '';
      position: absolute;
      top: 0;
      left: 0;
      right: 0;
      height: 5px;
      background: linear-gradient(90deg, var(--main-color), #5dade2);
    }

    .payment-container h2 {
      text-align: center;
      color: var(--main-color);
      margin-bottom: 30px;
      font-size: 1.8rem;
      font-weight: 700;
    }


    .bank-section {
      background: var(--secondary-color);
      border: 2px solid var(--main-color);
      border-radius: 15px;
      padding: 25px;
      margin-bottom: 30px;
      font-size: 1rem;
      color: var(--text-color);
      line-height: 1.6;
      position: relative;
    }

    .bank-section h3 {
      color: var(--main-color);
      margin-bottom: 15px;
      font-size: 1.3rem;
      text-align: center;
    }

    .bank-section p {
      margin: 10px 0;
      display: flex;
      align-items: center;
    }

    .bank-section p i {
      margin-right: 10px;
      color: var(--main-color);
    }


    .separator {
      border-top: 2px solid var(--border-color);
      margin: 30px 0;
      position: relative;
    }

    .separator::after {
      content: 'ðŸ”¹';
      position: absolute;
      top: -12px;
      left: 50%;
      transform: translateX(-50%);
      background: #fff;
      padding: 0 10px;
      color: var(--main-color);
      font-size: 1.2rem;
    }


    .form-section h3 {
      color: var(--main-color);
      margin-bottom: 20px;
      font-size: 1.3rem;
      text-align: center;
    }

    form {
      display: flex;
      flex-direction: column;
      gap: 20px;
    }

    .input-group {
      position: relative;
    }

    .input-group i {
      position: absolute;
      left: 12px;
      top: 50%;

      color: var(--main-color);
      font-size: 1.1rem;
    }

    label {
      font-weight: 600;
      color: var(--text-color);
      margin-bottom: 5px;
      display: block;
    }

    input[type="text"],
    input[type="email"],
    input[type="number"],
    input[type="file"] {
      padding: 12px 12px 12px 40px;
      border: 2px solid #e0e0e0;
      border-radius: 10px;
      font-size: 1rem;
      transition: 0.3s;
      width: 100%;
      box-sizing: border-box;
    }

    input[type="file"] {
      background-color: #fafafa;
      padding-left: 12px;
    }

    input:focus {
      border-color: var(--main-color);
      outline: none;
      box-shadow: 0 0 8px rgba(58, 134, 255, 0.3);
    }

    button {
      background: linear-gradient(135deg, var(--main-color), #5dade2);
      color: white;
      border: none;
      padding: 15px;
      font-size: 1.1rem;
      border-radius: 10px;
      cursor: pointer;
      transition: 0.3s;
      font-weight: 600;
      margin-top: 10px;
    }

    button:hover {
      transform: translateY(-2px);
      box-shadow: 0 6px 15px rgba(58, 134, 255, 0.4);
    }

    .note {
      font-size: 0.9rem;
      text-align: center;
      color: #777;
      margin-top: 15px;
      font-style: italic;
    }


    .popup {
      position: fixed;
      top: 0;
      left: 0;
      width: 100%;
      height: 100%;
      background: rgba(0, 0, 0, 0.5);
      display: flex;
      justify-content: center;
      align-items: center;
      visibility: hidden;
      opacity: 1;
      transition: 0.4s ease;
      z-index: 1000;

}


    .popup-content {
      background: white;
      padding: 30px 40px;
      border-radius: 20px;
      text-align: center;
      box-shadow: var(--shadow);
      animation: slideIn 0.4s ease;
      max-width: 400px;
      width: 90%;
    }

    .popup-content h3 {
      color: var(--main-color);
      margin-bottom: 15px;
      font-size: 1.5rem;
    }

    .popup-content p {
      color: #555;
      font-size: 1rem;
      margin-bottom: 25px;
    }

    .popup-content button {
      background: var(--main-color);
      color: #fff;
      border: none;
      padding: 12px 30px;
      border-radius: 10px;
      cursor: pointer;
      transition: 0.3s;
      font-weight: 600;
    }

    .popup-content button:hover {
      background: #2a6ed1;
      transform: scale(1.05);
    }

    @keyframes slideIn {
      from { transform: scale(0.8) translateY(-20px); opacity: 0; }
      to { transform: scale(1) translateY(0); opacity: 1; }
    }


    @media (max-width: 600px) {
      .payment-container {
        padding: 25px;
        margin: 10px;
      }
      .payment-container h2 {
        font-size: 1.5rem;
      }
      .bank-section {
        padding: 20px;
      }
      .popup-content {
        padding: 20px;
      }
    }
