commits.content:
:root {
  --main-color: #0075FF;
  --section-color: #F7F8FA;
  --font-color: #223A58;
  --main-transition-duration: 0.3s; 
}

.container{
    margin-left: auto;
    margin-right: auto;
    padding-left: 15px;
    padding-right: 15px;
}
/* Small */
@media (min-width: 768px) {
  .container {
    width: 750px;
  }
}
/* Medium */
@media (min-width: 992px) {
  .container {
    width: 970px;
  }
}
/* Large */
@media (min-width: 1200px) {
  .container {
    width: 1170px;
  }
}
.main-title {
  padding: 8px 15px;
  width: fit-content;
  position: relative;
  margin: 0 auto 80px;
  border: 2px solid black;
  font-size: 30px;
  z-index: 1;
  text-transform: uppercase;
  transition: var(--main-transition-duration);
}

.main-title:hover {
  color: white;
  border: 2px solid white;
  transition: 0.5s;
}

.main-title::before,
.main-title::after {
  content: "";
  position: absolute;
  background-color: var(--main-color);
  border-radius: 50%;
  width: 12px;
  height: 12px;
  top: 50%;
  transform: translateY(-50%);
}

.main-title::before {
  left: -30px;
}

.main-title::after {
  right: -30px;
}

.main-title:hover::before {
  z-index: -1;
  animation: move-left 0.5s linear forwards;
}

.main-title:hover::after {
  z-index: -1;
  animation: move-right 0.5s linear forwards;
}

@keyframes move-left {
  50% {
    left: 0;
    width: 12px;
    height: 12px;
  }
  100% {
    left: 0;
    height: 100%;
    width: 60%;
    border-radius: 0;
  }
}

@keyframes move-right {
  50% {
    right: 0;
    width: 12px;
    height: 12px;
  }
  100% {
    right: 0;
    height: 100%;
    width: 60%;
    border-radius: 0;
  }
}


ul {
  list-style: none;
}

a {
  text-decoration: none;
}
